	(299, 376, '2014_health_spa', '\'14 - Health Spa', 'default_3x3', 1, 211, 1, 14, '1', '1', '0', '0', 'valentines2014_header', 'valentines2014_teaser2', '', 'Sometimes, all you want is a nice relaxing spa day. With this furni line, you can enjoy one with your Valentine!', '', '', '', 0, ''),
	(25, 604, 'badge_display', 'Badge Display', 'badge_display', 1, 224, 1, 2, '1', '1', '0', '0', 'catalog_header_badgedisplay', 'badgedisplay_promo2', '', 'You worked hard to get your badges, right? Now\'s the time to show it off, grab a display case and choose your favorite badge to show off!', '', '', '', 0, ''),
